import { Component } from '@angular/core';
import Book from "../models/Book";

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent {
  book = new Book();
}
<div class="card">
  <form>
    <h1 class="card-title">Register a book</h1>
    <div class="card-segment">
      <label for="bookName">Book Name</label>
      <input type="text" min="3" placeholder="Book Name" id="bookName" name="Book Name" [(ngModel)]="book.name">
    </div>

    <div class="card-segment">
      <label for="authorName">Author Name</label>
      <input type="text" min="3" placeholder="Book Name" id="authorName" name="Author Name" [(ngModel)]="book.author">
    </div>
  </form>
  <div class="card-footer">
    <button class="btn-create">Create</button>
  </div>
</div>
