import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {catchError, Observable, throwError} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class ListbookService {

  private urlEndpoint: string = "http://localhost:8080/api/Books/";
  private httpHeaders = new HttpHeaders({'Content-Type' : 'application/json'});

  constructor(private http: HttpClient) { }

  public getAllBooks():Observable<any> {
    return this.http.get<any>(this.urlEndpoint).pipe(
      catchError(e => {
        window.alert(`${e.status} ${e.error}`);
        return throwError(() => e);
      }));
  }

  public delete(id:number): Observable<any> {
    return this.http.delete<any>(`${this.urlEndpoint}${id}`).pipe(
      catchError(e => {
        window.alert(e.body);
        return throwError(() => e);
      })
    );
  }
}
